mapper.select:
SELECT * FROM tournament WHERE del_flg = 0

	AND (name_tnm LIKE '%${name_tnm}%' OR info_tnm LIKE '%${name_tnm}%' OR start_date LIKE '%${name_tnm}%' OR end_date LIKE '%${name_tnm}%')

LIMIT  #{maxPageItem} 
OFFSET #{offset}

SELECT * FROM tournament WHERE del_flg = 0 AND status_flg = #{status_flg}

	AND (name_tnm LIKE '%${name_tnm}%' OR info_tnm LIKE '%${name_tnm}%' OR start_date LIKE '%${name_tnm}%' OR end_date LIKE '%${name_tnm}%')

LIMIT  #{maxPageItem} 
OFFSET #{offset}
